<?php


//add admin page and icon on panel menu
function scwp_setup_menu(){
    add_menu_page(
        'shabbat close wp',
        __('Shabbat Kodesh', 'scwp'),
        'manage_options',
        'shabbat_close_wp',
        'scwp_menu_content',
        'data:image/svg+xml;base64,',
        30
    );
}
add_action('admin_menu', 'scwp_setup_menu');


require_once plugin_dir_path( __FILE__ ) . '/includes/enqueue.php';
require_once plugin_dir_path( __FILE__ ) . '/includes/menu-page.php';
require_once plugin_dir_path( __FILE__ ) . '/shabbat.php';
require_once plugin_dir_path( __FILE__ ) . '/ajax.php';



function scwp_languages_init() {

    load_plugin_textdomain( 'scwp', false, 'shabbat-close-wp/languages/' );
}
add_action( 'init', 'scwp_languages_init' );

