//register settings fields 
function scwp_admin_settings() {
    register_setting( 'scwp_options', 'scwp_is_shabbat' );
    register_setting( 'scwp_options', 'scwp_time_zone');
    register_setting( 'scwp_options', 'scwp_is_woocommerce' );
    register_setting( 'scwp_options', 'scwp_is_alert_active' );
    register_setting( 'scwp_options', 'scwp_alert_msg_active' );
    register_setting( 'scwp_options', 'scwp_elementor_is_poup_active' );
    register_setting( 'scwp_options', 'scwp_elementor_pop_id');
}
add_action( 'admin_init', 'scwp_admin_settings' );

